XXAMMXXAMA
SMSMSASXSS
SAXAMASAAA
MAMMMXMMMM
MXMXAXMASX"""

val gridLR = in.linesIterator.toSeq
val s = gridLR.size
val gridUD = for(i <- 0 until s) yield (for(j <- 0 until s) yield gridLR(j)(i)).mkString

val diagCoords = for (i <- 0 until (2*s)) yield for(j <- 0 to i; if (i-j) < s && j < s) yield (j, i-j)

val diagU = diagCoords.map(_.map { case (x, y) => gridLR(x)(y) }.mkString)
val diagD = diagCoords.map(_.map { case (x, y) => gridLR(s-x-1)(y) }.mkString)

def countXMAS(str: String): Int = str.sliding(4).count(s => s == "XMAS" || s == "SAMX")
println(gridLR.map(countXMAS).sum + gridUD.map(countXMAS).sum + diagU.map(countXMAS).sum + diagD.map(countXMAS).sum)

def isMASX(i: Int, j: Int): Boolean = gridLR(i)(j) == 'A' && ((gridLR(i-1)(j-1) == 'M' && gridLR(i+1)(j+1) == 'S') || (gridLR(i-1)(j-1) == 'S' && gridLR(i+1)(j+1) == 'M')) && ((gridLR(i-1)(j+1) == 'M' && gridLR(i+1)(j-1) == 'S') || (gridLR(i-1)(j+1) == 'S' && gridLR(i+1)(j-1) == 'M'))
println((1 until (s-1)).map(i => (1 until (s-1)).count(j => isMASX(i, j))).sum)
